package ex02연산자;

public class Ex05증감연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//증감연산자
		//변수의 값을 1올리거나 1감소시키는 연산자
		int num =10;
		//증가
		System.out.println(++num);
		System.out.println(num);
		System.out.println(num++);
		System.out.println(num);
		
		int num2 = 10;
		System.out.println(--num2);
		System.out.println(num2);
		System.out.println(num2--);
		System.out.println(num2);

	}

}
