package ex01while;
public class ex04100까지의합 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		int total = 0;
		while(true) {
			total += i;
			i++;
			if (i==101) {
				System.out.print("1부터 100까지의 합은 : "+ total);
				break;
				
		//**while(i<=100){
		
			}
		}
	}
}
